import {Link} from "react-router-dom";
import CartTable from "./CartTable";
import {useContext} from "react";
import {CartStore} from "../contexts/CartContext";
import {CartTypes} from "../reducers/CartReducer";
import '../assets/css/Cart.css'

function Cart() {

    const {cart, dispatch} = useContext(CartStore);
    // Sum up the quantity of all cart items
    const totalQuantity = cart.reduce((sum, item) => sum + item.quantity, 0);

    const clearCart = () => {

        dispatch({type: CartTypes.CLEAR});

    };


    // Determine the correct string based on the number of items
    const bookString = totalQuantity === 1 ? 'book' : 'books';
    if (totalQuantity === 0) {
        return (
            <>
                <center><h1>Empty cart</h1></center>

                {/*<Link to={"/categories/Fiction"} style={{textDecoration:'none'}}>*/}
                {/*    <center>*/}
                {/*        <button className="navi-btn">Continue Shopping</button>*/}
                {/*    </center>*/}
                {/*</Link>*/}

            </>
        )
    } else {

        return (
            <>
                <center>
                    <div>
                        <h1>Shopping Cart</h1>
                        <h2> {totalQuantity} {bookString}
                        </h2>
                    </div>
                </center>

                <div>
                    {/*<h1>Cart Page</h1>*/}
                    <CartTable></CartTable>
                    <button className="tertiary-button" onClick={clearCart}>Clear Cart</button>
                </div>


                {/*<div className="login-btn-div">*/}
                {/*    <button className="login-btn">IAn</button>*/}
                {/*</div>*/}
                <div className="navi-section">

                    <Link to={"/checkout"} className="checkout-btn">
                        Proceed to Checkout
                    </Link>
                    <Link to={"/categories/Adventure"} className="continue-btn">
                        Continue Shopping
                    </Link>

                </div>

            </>

        )
    }
}

export default Cart;
