import {ShoppingCartItem, BookItem} from "../types";
// import {Dispatch, ReducerAction} from "react";


export const CartTypes = {
    ADD: 'ADD',
    REMOVE: 'REMOVE',
    CLEAR: 'CLEAR'
};

export type AppActions = {
    id: number;
    type: 'ADD' | 'REMOVE' | 'CLEAR';
    item: BookItem;
}

// this is used by the reducer. You can define it on the CartReducer
export const initialCartState: ShoppingCartItem[] = [];


const findItem = (cart: any[], id: number) => cart.find((item) => item.id === id);

export const cartReducer = (state: ShoppingCartItem[], action: AppActions) => {

    switch (action.type) {
        case CartTypes.ADD:


            if (findItem(state, action.item.bookId)) {

                return state.map((book) => book.id === action.item.bookId ? {
                    ...book,
                    quantity: book.quantity + 1
                } : book);
            } else {

                console.log("state",
                    ...state,
                    {id: action.id,items:action.item, quantity: 1 },
                );
                return [
                    ...state,
                    {
                        id: action.id,
                        items: action.item,
                        quantity: 1
                    }
                ];
            }



        case CartTypes.REMOVE:

            /*
               will be defiend in Project 7
             */
            if (findItem(state, action.book.bookId)) {
                const newState = state.map((book) =>
                    book.id === action.book.bookId
                        ? { ...book, quantity: book.quantity - 1 }
                        : book
                )
                    .filter((book) => book.quantity >= 1);

                localStorage.setItem('cart', JSON.stringify(newState));
                return newState
                // return state.map((book) =>
                //         book.id === action.book.bookId
                //             ? { ...book, quantity: book.quantity - 1 }
                //             : book
                //     )
                //     .filter((book) => book.quantity >= 1);
            }else{
                localStorage.setItem('cart', JSON.stringify(state));
                return state
            }


        case CartTypes.CLEAR:
            const newState = initialCartState
            localStorage.setItem('cart', JSON.stringify(newState));
            // state = initialCartState;
            return newState;
        default:
            throw new Error(`Invalid action type ${action.type}`);
    }
};
